
import chardet
from fake_useragent import UserAgent
from urllib import request,parse
import pymysql
#获取页面
def get_html(url):
    form_data={
        '__EVENTTARGET':'ctl00$ContentPlaceHolder1$GridView1',
        '__EVENTARGUMENT':'Page$3',
        '__VIEWSTATEENCRYPTED':"",
        '__VIEWSTATE':''

    }


    ua=UserAgent()
    headers={'User_agent':ua.random}

    req=request.Request(url,headers=headers,data=parse.urlencode(form_data).encode('utf-8'))
    response=request.urlopen(req).read()
    encoding=chardet.detect(response).get('encoding')
    htmls=response.decode(encoding)
    return htmls


